/************************************************************************************************************************************************************************\
 *** FUNCTION:
 ***     VEOS_BusApi_LinCalculateChecksum
 *** 
 *** DESCRIPTION:
 ***     API function: Calculates the checksum of a LIN message according to LIN specification.
 *** 
 *** PARAMETERS:
 ***     Type                Name          Description
 ***     ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***     const VEOS_uint8    InitialValue  Incoming argument: Initial value for the checksum calculation. When used in classic mode, the value must be set to 0. In
 ***                                       enhanced mode, the value must be set to message pid.
 ***     const VEOS_uint8 *  pData         Incoming argument: The data to calculate the checksum from.
 ***     const VEOS_uint32   Dlc           Incoming argument: The length of the data to calculate the checksum from.
 ***     VEOS_uint8 *        pChecksum     Outgoing argument: The calculated checksum.
 *** 
 *** RETURNS:
 ***     VEOS_ApiSuccessType    If no error occurs, VEOS_E_OK will be returned. If any error occurs, an error code will be the result. See VEOS_Types.h for details.
\************************************************************************************************************************************************************************/
#if VEOS_BUSAPI_LINSUPPORT == VEOS_ON
VEOS_ApiSuccessType VEOS_BusApi_LinCalculateChecksum(
    const VEOS_uint8 InitialValue,
    const VEOS_uint8 * pData,
    const VEOS_uint32 Dlc,
    VEOS_uint8 * pChecksum)
{
    /* Local result of API function */
    VEOS_ApiSuccessType success = VEOS_E_PENDING;

    /* Definition of local variables */
    /* The calculated checksum. */
    VEOS_uint8 checksum;
    /* The index. */
    VEOS_uint32 current;
    /* The current data value. */
    VEOS_uint16 dataValue;

    /* Track method start to debugging API */
    VEOS_FRM_DBG_METHOD_START(VEOS_API_NAME, __FUNCTION__);

    #ifndef VEOS_NOCHECKS
    /* Check pData to be a valid pointer. */
    VEOS_CHECK_PARAM_PTR(pData);
    /* Check pChecksum to be a valid pointer. */
    VEOS_CHECK_PARAM_PTR(pChecksum);
    #endif

    /* Start of custom implementation from configuration file. */
    /* 
     * LIN 2.0: Initial value must be protected identifier
     * LIN 1.3 or earlier: initial value must be 0, since only data is used.
     */
    checksum = InitialValue;
    for (current = 0; current < Dlc; current++)
    {
        /*
         * Software algorithm: 
         *     (*) Add current data value to existing checksum
         *     (*) Add each time, the data exceeds uint8 value range (> 255 = 0xFF)
         *     (**)  subtract 255 (do not calculate mod -255 or mod -256)
         * Hardware algorithm:
         *     (*) Add current data value to existing checksum.
         *     (*) Value range check.
         *     (**) Each time the nineth bit is set (0xFF is exceeded), add the value of 1 (which is equivalent to -255 (but faster)) due to uint8 value range.
         *     (***) Note that, the tenth bit cannot be set, since the addition of two uint8 variable can never exceed 510. Therefore, 0x0100 is enough.
         *     (**) If the nineth bit is not set, the shift operation will result in 0 and no addition will take place.
         */
        dataValue = checksum + pData[current];
        checksum = (VEOS_uint8)(dataValue + ((dataValue & 0x0100) >> 8));
    }
    /* Checksum calculation requires an inversion after all data values are taken into account. */
    VEOS_COPY_OUT_PARAM_SCALAR(pChecksum, ~checksum);
    success = VEOS_E_OK;
    /* End of custom implementation from configuration file. */

    /* Track method end to debugging API */
    VEOS_FRM_DBG_METHOD_END(VEOS_API_NAME, __FUNCTION__);

    /* Track method result to debugging API */
    VEOS_FRM_DBG_METHOD_RESULT(VEOS_API_NAME, __FUNCTION__, success);

    /* Return result to caller. */
    return success;
}
#endif


